package week_2;

public class day_1_polymorphism extends multiply{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Product of 2 whole numbers : "+ Multiply(32,87));
		System.out.println("Product of 1 whole and 1 decimal number : "+ Multiply(32,8.7));
		System.out.println("Product of 2 decimal numbers : "+ Multiply(3.2,8.7));
		System.out.println("Product of 3 whole numbers : "+ Multiply(32,8,5));
	}

}
